class Solution:
    # @param s, a string
    # @return an integer
    def minCut(self, s):
        if len(s) <= 1:
            return 0
        length = len(s)
        minc = []
        for i in range(length+1):
            minc.append(length-i)
        matrix = [[False]*length for _ in range(length)]
        for i in range(length-1, -1, -1):
            for j in range(i, length):
                if s[i] == s[j] and (j - i < 2 or matrix[i+1][j-1]):
                    matrix[i][j] = True
                    minc[i] = min(minc[i], minc[j+1]+1)
        return minc[0]-1
